import './App.css';
import { useState, useEffect } from 'react'
import axios from 'axios'

const Filter = ({ newFilter, handleFilter }) => {
  return (
    <p>
      find countries <input value={newFilter} onChange={handleFilter} />
    </p>
  )
}

const ShowInfos = ({ newFilter, countries, setUrl }) => {
  console.log(countries)
  console.log('filter in showInfos', newFilter)

  if (newFilter) {
    const show = countries.filter(country => country.name.common.includes(newFilter))
    console.log('show = ', show)

    if (show.length === 1) {
      console.log('lang', show[0].languages)
      setUrl(show[0].flag.png)
      return (
        <div>
          <h1> {show[0].name.common} </h1>
          <p> capital {show[0].capital} </p>
          <p> area {show[0].area}</p>
          <h2> languages: </h2>
          <ul>
            {Object.entries(show[0].languages).map(item =>
              <li key={item}> {(Object.entries(item)[1][1])} </li>)}
          </ul>
          <p> {show[0].flags.png} </p>
        </div>
      )
    }
    if (show.length > 0 && show.length <= 10) {
      console.log('match found')
      return (
        <div>
          {show.map(f => <p key={f.name.common}> {f.name.common} </p>)}
        </div>
      )
    }
    else {
      console.log('too many entries')
      return (
        <p>
          Too many matches, specify another filter
        </p>
      )
    }
  }
}

const App = () => {
  const [newFilter, setNewFilter] = useState('')
  const [countries, setCountries] = useState([])
  const [flag, setFlag] = useState({})
  const [url, setUrl] = useState('')

  const handleFilter = (event) => {
    console.log('event target value', event.target.value)
    setNewFilter(event.target.value)
  }

  const hook = () => {
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        console.log(response.data)
        setCountries(response.data)
      })
  }
  useEffect(hook, [])

  const get_flag = () => {
    if (url) {
      axios
        .get(url)
        .then(response => {
          console.log(response.data)
          setFlag(response.data)
        })
    }
  }
  useEffect(get_flag(), [])

  return (
    <div>
      <Filter newFilter={newFilter} handleFilter={handleFilter} />
      <ShowInfos newFilter={newFilter} countries={countries} setUrl={setUrl} flag={flag} />
    </div>
  )
}

export default App;
