const Header = (props) => (
  <h1>{props.course}</h1>
)

const Part = ({ part, exercise }) => (
  <p> {part} {exercise} </p>
)

const Content = ({ part }) => {
  return (
    <div>
      <Part part={part[0].name} exercise={part[0].exercise}/>
      <Part part={part[1].name} exercise={part[1].exercise}/>
      <Part part={part[2].name} exercise={part[2].exercise}/>
    </div>
  )
}

const Total = ({ part }) => (
  <p>
    Number of exercises {
      part[0].exercise + part[1].exercise + part[2].exercise
    }
  </p>
) 

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercise: 7
  }

  const part3 = {
    name: 'State of a component',
    exercises: 14
  }
  const part = [part1, part2, part3]

  return (
    <div>
      <Header course={course} />
      <Content part={part}/>
      {/*<Total exercise={part} />*/}
    </div>
  )
}

export default App
