const Header = (props) => (
  <h1>{props.course}</h1>
)

const Part = ({ part, exercise }) => (
  <p> {part} {exercise} </p>
)

const Content = ({ parts }) => {
  return (
    <div>
      {parts.map(part => <Part part={part.name} exercise={part.exercise} />)}
    </div>
  )
}

const Total = ({ parts }) => {
  const res = parts.map(part => part.exercises)

  return (
    <h2>
      total of exercises { res[0] + res[1] + res[2] }
    </h2>
  )
}

const Course = ({ course }) => {
  return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </div>
  )
}

const App = () => {
  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'Redux',
        exercises: 11,
        id: 4
      }
    ]
  }

  return <Course course={course} />
}

export default App
