const Header = (props) => (
  <h1>{props.course}</h1>
)

const Part = ({ part, exercise }) => (
  <p> {part} {exercise} </p>
)

const Content = ({ part, exercise }) => {
  return (
    <div>
      <Part part={part[0]} exercise={exercise[0]}/>
      <Part part={part[1]} exercise={exercise[1]}/>
      <Part part={part[2]} exercise={exercise[2]}/>
    </div>
  )
}

const Total = ({ exercise }) => (
  <p>Number of exercises {exercise[0] + exercise[1] + exercise[2]}</p>
) 

const App = () => {
  const course = 'Half Stack application development'
  const part = [
    'Fundamentals of React',
    'Using props to pass data',
    'State of a component'
  ]
  const exercise = [10, 7, 14]

  return (
    <div>
      <Header course={course} />
      <Content part={part} exercise={exercise} />
      <Total exercise={exercise} />
    </div>
  )
}

export default App
