const Header = (props) => (
  <h1>{props.course}</h1>
)

const Content = (props) => {
  return (
    <div>
      <p>
        {props.part[0]} {props.exercise[0]}
      </p>
      <p>
        {props.part[1]} {props.exercise[1]}
      </p>
      <p>
        {props.part[2]} {props.exercise[2]}
      </p>
    </div>
  )
}

const Total = ({exercise}) => (
  <p>Number of exercises {exercise[0] + exercise[1] + exercise[2]}</p>
) 

const App = () => {
  const course = 'Half Stack application development'
  const part = [
    'Fundamentals of React',
    'Using props to pass data',
    'State of a component'
  ]
  const exercise = [10, 7, 14]

  return (
    <div>
      <Header course={course} />
      <Content part={part} exercise={exercise} />
      <Total exercise />
    </div>
  )
}

export default App
