const Header = (props) => (
  <h1>{props.course}</h1>
)

const Part = ({ part, exercise }) => (
  <p> {part} {exercise} </p>
)

const Content = ({ parts }) => {
  return (
    <div>
      <Part parts={parts[0].name} exercise={parts[0].exercises}/>
      <Part parts={parts[1].name} exercise={parts[1].exercises}/>
      <Part parts={parts[2].name} exercise={parts[2].exercises}/>
    </div>
  )
}

const Total = ({ parts }) => (
  <p>
    Number of exercises {
      part[0].exercises + part[1].exercises + part[2].exercises
    }
  </p>
) 

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header course={course} />
      <Content parts={parts}/>
      <Total parts={parts} />
    </div>
  )
}

export default App
