import React from 'react'

const DisplayParts = (props) => {
    console.log(props)
    const parts = props.parts
    const total = parts.reduce((p, c) => {
        return p + c.exercises;
    }, 0)
    return (
        <div>
            {parts.map(part =>
                <p key={part.id}>
                    {part.name} {part.exercises}
                </p>
            )}
            <h3> total of {total} exercises </h3>
        </div>
    )
}

const DisplayCourses = (props) => {
    console.log(props)
    const courses = props.courses
    return (
        <div>
            <h1> Web development curriculum </h1>
            {courses.map(course =>
                <div key={course.id}>
                    <h2>{course.name}</h2>
                    <DisplayParts parts={course.parts} />
                </div>
            )}
        </div>
    )
}

const Course = (props) => {
    console.log(props)
    const courses = props.courses

    return (
        <div>
            <DisplayCourses courses={courses} />
        </div>
    )
}

export default Course
