import { useState } from 'react'

const Filter = (props) => {
  console.log(props)
  return (
    <p>
      filter shown with <input onChange={props.handleFilter} />
    </p>
  )
}

const PersonForm = (props) => {
  console.log(props)
  return (
    <form>
      <div>
        name: <input value={props.states[newName]} onChange={props.handlers[handleNameInput]} />
      </div>
      <div>
        number: <input value={props.states[newNumber]} onChange={props.handlers[handleNumberInput]} />
      </div>
      <div>
        <button onClick={props.handlers[addPerson]} type="submit">add</button>
      </div>
    </form>
  )
}

const App = () => {
  // states
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [searchName, setSearchName] = useState('')
  const handlers = [
    { state: 'name', handler: handleNameInput, id: 'name'},
    { state: 'number', handler: handleNumberInput, id: 'number'},
    { state: 'person', handler: addPerson, id: 'person'} 
  ]

  // handle name input
  const handleNameInput = (event) =>
  {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  // handle number input
  const handleNumberInput = (event) =>
  {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  // handle add person
  const addPerson = (event) =>
  {
    event.preventDefault()
    console.log('button clicked', event.target)
    const found = persons.find(person => person.name === newName)

    if (found === undefined) {
      const nameObject = {
        name: newName,
        number: newNumber,
      }
      setPersons(persons.concat(nameObject))
      setNewName('')
      setNewNumber('')
    }
    else
      alert(`${newName} is already added to phonebook`)
  }

  // handle filter
  const handleFilter = (event) => {
    console.log(event.target.value)
    setSearchName(event.target.value)
  }

  // persons to show
  const personsToShow = searchName.length
    ? persons.filter(person => person.name.toLowerCase().indexOf(searchName.toLowerCase()) !== -1)
    : persons

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter handleFilter={handleFilter} />
      <h2>add a new</h2>
      <PersonForm handlers={handlers} />
      <h2>Numbers</h2>
      {personsToShow.map(person =>
        <p key={person.name}>{person.name} {person.number}</p>
      )}
    </div>
  )
}

export default App;
