import { useState, useEffect } from 'react'
import axios from 'axios'

const Filter = ({ newFilter, handleNewFilter }) => {
  return (
    <p>
      filter shown with <input value={newFilter} onChange={handleNewFilter} />
    </p>
  )
}

const PersonForm = ({ addPerson, newName, newNumber, handleNewName, handleNewNumber }) => {
  return (
    <form onSubmit={addPerson}>
      <div>
        name: <input value={newName} onChange={handleNewName} />
      </div>
      <div>
        number: <input value={newNumber} onChange={handleNewNumber} />
      </div>
      <div>
        <button type="submit">add</button>
      </div>
    </form>
  )
}

const DisplayInfos = ({ person }) => {
  return (
    <p> {person.name} {person.number} </p>
  )
}

const Persons = ({ persons }) => {
  return (
    <div>
      {
        persons.map(person =>
          <DisplayInfos key={person.name} person={person} />)
      }
    </div>
  )
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-1234567' },
    { name: 'Ada Lovelace', number: '39-44-5323523' }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewfilter] = useState('')

  const addPerson = (event) => {
    event.preventDefault()
    const newPerson = {
      name: newName,
      number: newNumber
    }
    if (persons.find(person => person.name === newName))
      alert(`${newName} is already added to phonebook`)
    else {
      setPersons(persons.concat(newPerson))
      setNewName('')
      setNewNumber('')
    }
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleNewFilter = (event) => {
    setNewfilter(event.target.value)
  }

  const show = newFilter === ''
    ? persons
    : persons.filter(person => person.name.toLowerCase().includes(newFilter.toLowerCase()))

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter newFilter={newFilter} handleNewFilter={handleNewFilter} />

      <h3>add a new</h3>
      <PersonForm 
        addPerson={addPerson}
        newName={newName}
        newNumber={newNumber}
        handleNewName={handleNewName}
        handleNewNumber={handleNewNumber} />

      <h2>Numbers</h2>
      <Persons persons={show} />

    </div>
  )
}

export default App
